import React, { useState, useEffect } from 'react';
import './Ranking.css';
import GameService from '../services/GameService';

const Ranking = () => {
  const [highScores, setHighScores] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadHighScores();
  }, []);

  const loadHighScores = async () => {
    try {
      setLoading(true);
      const scores = await GameService.getHighScores();
      setHighScores(scores);
      setLoading(false);
    } catch (error) {
      console.error("Erro ao carregar pontuações:", error);
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  if (loading) {
    return <div className="loading">Carregando pontuações...</div>;
  }

  return (
    <div className="ranking">
      <h2>Melhores Pontuações</h2>
      {highScores.length === 0 ? (
        <p>Ainda não há pontuações registradas.</p>
      ) : (
        <table className="score-table">
          <thead>
            <tr>
              <th>Posição</th>
              <th>Movimentos</th>
              <th>Data</th>
            </tr>
          </thead>
          <tbody>
            {highScores.map((score, index) => (
              <tr key={score.id} className={index === 0 ? 'top-score' : ''}>
                <td>{index + 1}</td>
                <td>{score.moves}</td>
                <td>{formatDate(score.date)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <button onClick={loadHighScores} className="refresh-button">
        Atualizar
      </button>
    </div>
  );
};

export default Ranking;
