// Servi√ßo de temas para o jogo da mem√≥ria
// Usando simula√ß√£o em localStorage para desenvolvimento

class ThemeService {
  constructor() {
    this.initializeDefaultThemes();
  }

  // Obter todos os temas
  getAllThemes() {
    return new Promise((resolve) => {
      const themes = this.getThemesFromStorage();
      resolve(themes);
    });
  }

  // Obter um tema espec√≠fico pelo ID
  getThemeById(themeId) {
    return new Promise((resolve, reject) => {
      const themes = this.getThemesFromStorage();
      const theme = themes.find(t => t.id === parseInt(themeId) || t.id === themeId);
      
      if (theme) {
        resolve(theme);
      } else {
        reject(new Error(`Tema com ID ${themeId} n√£o encontrado`));
      }
    });
  }

  // Obter cartas para um tema e dificuldade espec√≠ficos
  getCardsForTheme(themeId, difficulty) {
    return new Promise((resolve, reject) => {
      try {
        const theme = this.getThemesFromStorage().find(t => 
          t.id === parseInt(themeId) || t.id === themeId
        );
        
        if (!theme) {
          return reject(new Error(`Tema com ID ${themeId} n√£o encontrado`));
        }
        
        // Determinar n√∫mero de pares com base na dificuldade
        let pairsCount;
        switch (difficulty) {
          case 'easy':
            pairsCount = 4; // 8 cartas
            break;
          case 'medium':
            pairsCount = 8; // 16 cartas
            break;
          case 'hard':
            pairsCount = 12; // 24 cartas
            break;
          default:
            pairsCount = 8;
        }
        
        // Garantir que temos s√≠mbolos suficientes
        if (theme.symbols.length < pairsCount) {
          return reject(new Error(`Tema n√£o tem s√≠mbolos suficientes para a dificuldade ${difficulty}`));
        }
        
        // Selecionar s√≠mbolos aleatoriamente para o jogo
        const gameSymbols = this.getRandomElements(theme.symbols, pairsCount);
        
        // Criar pares de cartas (duplicando cada s√≠mbolo)
        let cards = [];
        gameSymbols.forEach((symbol, index) => {
          // Primeira carta do par
          cards.push({
            id: `card-${index}-a`,
            value: symbol,
            isFlipped: false,
            isMatched: false
          });
          
          // Segunda carta do par
          cards.push({
            id: `card-${index}-b`,
            value: symbol,
            isFlipped: false,
            isMatched: false
          });
        });
        
        // Embaralhar as cartas
        cards = this.shuffleArray(cards);
        
        resolve(cards);
      } catch (error) {
        reject(error);
      }
    });
  }

  // Fun√ß√µes auxiliares
  getThemesFromStorage() {
    const themesStr = localStorage.getItem('memory_themes');
    return themesStr ? JSON.parse(themesStr) : [];
  }

  getRandomElements(array, count) {
    const shuffled = [...array].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  }

  shuffleArray(array) {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  }

  // Inicializar temas padr√£o para desenvolvimento
  initializeDefaultThemes() {
    if (!localStorage.getItem('memory_themes')) {
      const defaultThemes = [
        {
          id: 1,
          name: 'Emoji',
          description: 'Tema com emojis divertidos',
          symbols: ['üòÄ', 'üòé', 'ü•≥', 'üöÄ', 'üéÆ', 'üçï', 'üê±', 'üê∂', 'üåà', 'üåü', 'üéµ', 'üéÅ', 'üèÜ', 'üç¶']
        },
        {
          id: 2,
          name: 'Animais',
          description: 'Tema com animais',
          symbols: ['üê∂', 'üê±', 'üê≠', 'üêπ', 'üê∞', 'ü¶ä', 'üêª', 'üêº', 'üê®', 'ü¶Å', 'üêØ', 'üê∏', 'üêµ', 'ü¶Ñ']
        },
        {
          id: 3,
          name: 'Comidas',
          description: 'Tema com comidas',
          symbols: ['üçé', 'üçå', 'üçì', 'üçï', 'üçî', 'üç¶', 'üç©', 'üç™', 'üç´', 'üçø', 'ü•ó', 'üç£', 'üßÅ', 'ü•û']
        }
      ];
      
      localStorage.setItem('memory_themes', JSON.stringify(defaultThemes));
    }
  }
}

export default new ThemeService();
