import axios from 'axios';
import AuthService from './AuthService';

const API_BASE_URL = 'http://localhost:8080/api/themes';

const ThemeService = {
  getAllThemes: async () => {
    try {
      const response = await axios.get(API_BASE_URL, {
        headers: AuthService.getAuthHeader()
      });
      return response.data;
    } catch (error) {
      console.error('Erro ao buscar temas:', error);
      throw error;
    }
  },

  getThemeById: async (id) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/${id}`, {
        headers: AuthService.getAuthHeader()
      });
      return response.data;
    } catch (error) {
      console.error('Erro ao buscar tema:', error);
      throw error;
    }
  },

  getCardsForTheme: async (themeId, difficulty = 'medium') => {
    try {
      const response = await axios.get(`${API_BASE_URL}/${themeId}/cards`, {
        params: { difficulty },
        headers: AuthService.getAuthHeader()
      });
      return response.data;
    } catch (error) {
      console.error('Erro ao buscar cartas para o tema:', error);
      throw error;
    }
  }
};

export default ThemeService;
